mapper.insert:
insert into am_bs_group_prov (PK_BS_GROUP_PROV, PK_BS_GROUP, PK_BS_PROV, 
  MATCH_TYPE, PK_CREATOR, CREATE_TIME
  )
values (#{pkBsGroupProv,jdbcType=CHAR}, #{pkBsGroup,jdbcType=CHAR}, #{pkBsProv,jdbcType=CHAR}, 
  #{matchType,jdbcType=SMALLINT}, #{pkCreator,jdbcType=CHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT a.`PK_BS_GROUP`,a.`BS_NAME` AS G_BS_NAME,c.`PROV_CODE`,f.`PROV_NAME` ,c.`PK_BS_PROV`,c.`BS_NAME` AS P_BS_NAME ,d.`PK_ASSET`,d.`ASSET_NAME`,d.`ASSET_IP`,d.`PK_ASSET_TYPE`,e.`TYPE_NAME` FROM `am_bs_group` a , `am_bs_group_prov` b ,`am_bs_prov` c ,`am_asset` d,`am_asset_type` e,`bdm_prov` f
AND b.`PK_BS_GROUP` = #{groupId} AND a.`PK_BS_GROUP` = b.`PK_BS_GROUP` AND b.`PK_BS_PROV` = c.`PK_BS_PROV` AND d.`PK_PROV_BS` = c.`PK_BS_PROV` AND d.`PK_ASSET_TYPE` = e.`PK_ASSET_TYPE` AND c.`PROV_CODE` = f.`PROV_CODE`
AND c.`PROV_CODE` = #{prov}
AND d.`PK_ASSET_TYPE` = #{assetType}
AND d.`ASSET_NAME` LIKE '%${assetKeyWords}%'
